import { useEffect, useState } from "react";

function Card() {
  let [cardDetails, setCardDetails] = useState("Loading");
  useEffect(function () {
    async function fetchUserData() {
      try {
        const responseData = await fetch("https://randomuser.me/api/");
        if (responseData.status === 200) {
          const jsonData = await responseData.json();
          const {
            name: { title, first, last },
            picture: { medium: mediumImg },
            email,
          } = jsonData.results[0];
          const fullName = `${title} ${first} ${last}`;
          console.log(fullName);
          console.log(jsonData.results[0]); //its an Object-->name,image,email.
          // const img = await fetch(
          //   "https://randomuser.me/api/portraits/med/men/40.jpg"
          // ).then((response) => response.blob()); //Don't validate CORS for this fetch.
          // console.log(img);
       
          setCardDetails(  <div>
            <div>{fullName}</div>
            <div>{email}</div>
            <div>{mediumImg}</div>
            {/* <div>{img.blob()}</div> */}
          </div>);
        } else throw new Error("Data Not Fetched");
      } catch (error) {
        console.log(`error: ${error}`);
      }
    }
    fetchUserData(); 
  }, []);

  return (
    <>
      <div className="card">{cardDetails}</div>
    </>
  );
}

export default Card;
