        if(responseData.status===200)
        {
           const jsonData= await responseData.json()
           const {name:{title, first, last}, picture:{medium}, email}= jsonData.results[0]
        //    const fullName= `${name.title} ${ name.first} ${name.last}`; 
        //    console.log(fullName)
           console.log(jsonData.results[0]); //its an Object-->name,image,email. 
        //    setCardDetails()
        }
        else throw "Data Not Fetched"
      }
    catch(error){
        console.log(`error: ${error}`); 
    }
  }, []);

  return <>
  {cardDetails}
  </>
  ;
}

