import React, { useEffect, useState } from "react";
import storeData from "../State/store";
import InitialDetail from "./InitialDetail";
import MoreDetail from "./MoreDetail";
import cardStyle from "../CSS/Card.module.css";
import { BrowserRouter as Router, Link, Routes, Route } from "react-router-dom";

function Card() {
  const [userData, setUserData] = useState(null);

  useEffect(function () {
    async function loadData() {
      const data = await storeData();
      setUserData(data);
    }
    loadData();
  }, []);

  // to show data from redux store; 
  console.log(userData);

  return (
    <div className={cardStyle.card}>
      <Router>

      
      <Link to={`/detail`} className={cardStyle.noStyle}>
        <Routes>
          <Route
            path="/"
            element={
              <InitialDetail
                userImg={userData?.picture?.medium}
                userName={userData?.name}
                userEmail={userData?.email}
              />
            }
          />
        </Routes>
      </Link>

      <Routes>
        <Route
          path={`/detail`}
          element={
            <MoreDetail
              userImg={userData?.picture?.large}
              userName={userData?.name}
              dob={userData?.dob}
              userLocation={userData?.location}
              userEmail={userData?.email}
              userTel={userData?.cell}
            />
          }
        />
      </Routes>
      </Router>
    </div>
  );
}

export default Card;
