// import initialStyle from "../CSS/InitialDetail.module.css";
// (name,image,email,address,DOB,phoneNumber)

function MoreDetail(props) {
  if (props.userName) {
    const userImg = props.userImg;
    const userName = `${props.userName.title} ${props.userName.first} ${props.userName.last}`;
    const userDob = props.dob;

    // contact info
    const location = props.userLocation;
    const userAddress = `${location.street.number} ${location.street.name}\, ${location.city}\, ${location.country}`;
    console.log(userAddress);
    const userEmail = props.userEmail;
    const userTel = props.userTel;

    return (
      <div
        className={`${initialStyle["initial-detail"]} ${initialStyle.centre}`}
      >
        <img src={userImg} alt="User" className={initialStyle.userImg} />
        <p>{userName}</p>
     
        <p /* className={initialStyle.dob} */>
          <span className="label">DOB:</span>
          <span className="value"> {userDob} </span>
        </p>
        <p /* className={initialStyle.userAddress} */>
          <span className="label">Address:</span>
          <span className="value">{userAddress}</span>
        </p>
        <p /* className={initialStyle.userEmail} */>
          <span className="label">Email:</span>
          <span className="value">{userEmail}</span>
        </p>
        <p /* className={initialStyle.userTel} */>
          <span className="label">Tel:</span>
          <span className="value">{userTel}</span>
        </p>
      </div>
    );
  } else {
    return (
      <center /* className={`${initialStyle.loading}  ${initialStyle.centre}`} */>
        Loading...
      </center>
    );
  }
}

export default InitialDetail;
