import { useEffect, useState } from "react";

function UserCard() {

    const [cardDetails, setCardDetails]= useState('Loading')
  useEffect(async function () {
      try{
        const responseData = await fetch("https://randomuser.me/api/");

        if(responseData.status===200)
        {
           const jsonData= await responseData.json()
           const {id:{name}, image, email}= jsonData.results[0]
           console.log(image); //its an Object-->name,image,email. 
           setCardDetails(name)
        }
        else throw "Data Not Fetched"
      }
    catch(error){
        console.log(`error: ${error}`); 
    }
  }, []);

  return <>
  {cardDetails}
  </>
  ;
}

export default UserCard;
