import { useEffect, useState } from "react";

function UserCard() {

    let [cardDetails, setCardDetails]= useState('Loading')
  useEffect(async function () {
      try{
        const responseData = await fetch("https://randomuser.me/api/");

        if(responseData.status===200)
        {
           const jsonData= await responseData.json()
           const {name:{title, first, last}, picture:{medium:mediumImg}, email}= jsonData.results[0];
            const fullName= `${title} ${first} ${last}`; 
           console.log(fullName)
           console.log(jsonData.results[0]); //its an Object-->name,image,email. 
           cardDetails= <div>
           <div>{fullName}</div>
                     <div>{email}</div>
                     <div>{mediumImg}</div>
                     </div>
           setCardDetails(cardDetails) 

          
        }
        else throw "Data Not Fetched"
      }
    catch(error){
        console.log(`error: ${error}`); 
    }
  }, []);

  return <>
 <div>
   {cardDetails}
 </div>
  </>
  ;
}

export default UserCard;
