import moreStyle from "../CSS/MoreDetail.module.css";
// (name,image,email,address,DOB,phoneNumber)

function MoreDetail(props) {
  if (props?.userName) {
    const userImg = props.userImg;
    const userName = `${props.userName.title} ${props.userName.first} ${props.userName.last}`;
    const dob= new Intl.DateTimeFormat(navigator.language, {day:'numeric',month: 'long', year:'numeric'}).format(new Date(props.dob.date)); 
    console.log(dob);
    const userDob = `${props.dob.date}`;

    // contact info
    const location = props.userLocation;
    const userAddress = `${location.street.number} ${location.street.name}, ${location.city}, ${location.country}`;
    console.log(userAddress);
    const userEmail = props.userEmail;
    const userTel = props.userTel;

    return (
      <div className={`${moreStyle["more-detail"]} ${moreStyle.centre}`}>
        <div  >
          <img
            src={userImg}
            alt="User" 
            className={moreStyle.userImg}
          />
        </div>

        <div className={moreStyle.content}>
          <p  className={moreStyle.userName}>{userName}</p>
          <p className={moreStyle.property}>
            <span className={moreStyle.label}>DOB</span>
            <span className={moreStyle.value}> {userDob} </span>
          </p>
          <p className={moreStyle.property}>
            <span className={moreStyle.label}>Address</span>
            <span className={moreStyle.value}>{userAddress}</span>
          </p>
          <p className={moreStyle.property}>
            <span className={moreStyle.label}>Email</span>
            <span className={moreStyle.value}>{userEmail}</span>
          </p>
          <p className={moreStyle.property}>
            <span className={moreStyle.label}>Tel</span>
            <span className={moreStyle.value}>{userTel}</span>
          </p>
        </div>
      </div>
    );
  } else {
    return (
      <center className={`${moreStyle.loading}  ${moreStyle.centre}`}
      >
        Loading...
      </center>
    );
  }
}

export default MoreDetail;
