  async function loadData(){
    const data= await storeData();
    const cardDetails= ReactDom.render(<InitialDetail userImg={data.picture.medium} userName={data.name} userEmail={data.email}/>, document.querySelector('.card')); 
    setUserData(cardDetails);
  }

  useEffect(function(){
    loadData(); 
